    <div >-{Author}</div>
  )
}
class NewQuote extends React.Component {
  constructor(props){
    super(props);
  }
  render() {
    return (

      <button id="new-quote" onClick={console.log('clicked')}>This button gives a new quote</button>
    );
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >

          Learn React
        </a>
        {quoteBox()}
      </header>

    </div>
  );
}

export default App;
