import React from 'react';
import logo from './logo.svg';
import './App.css';
import Bahaquotes from './bahaQuotes.json';
import Abdulquotes from './abdulQuotes.json';
import Babquotes from './babQuotes.json';
import Gaurdianquotes from './gaurdianQuotes.json';
import UHJquotes from './uhjQuotes.json';



let text = "The betterment of the world can be accomplished..."
let Author = "Baha'u'llah"

const quotes = [...Bahaquotes, ...Abdulquotes, ...Babquotes, ...Gaurdianquotes, ...UHJquotes];

console.log(quotes.length);

function returnRandom(array){
  let random = Math.floor(Math.random() * Math.floor(array.length));
  return array[random];
}



function quoteBox(props){
  return (
    <div id="quote-box">
      {quoteText()}
      {authorName()}
      <NewQuote />
    </div>
  )
}

function quoteText(){
  return (
    <div id='text' >"{returnRandom(quotes).quote}"</div>
  )
}

function authorName(){
  return (
    <div >-{Author}</div>
  )
}
class NewQuote extends React.Component {
  constructor(props){
    super(props);
  }
  render() {
    return (

      <button id="new-quote" onClick={console.log('clicked')}>This button gives a new quote</button>
    );
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >

          Learn React
        </a>
        {quoteBox()}
      </header>

    </div>
  );
}

export default App;
